sysauto_script.script:
try { 
 var r = new sn_ws.RESTMessageV2('x_58872_needit.Federal Register API - Agencies', 'Default GET');

//override authentication profile 
//authentication type ='basic'/ 'oauth2'
//r.setAuthentication(authentication type, profile name);

//set a MID server name if one wants to run the message on MID
//r.setMIDServer('MY_MID_SERVER');

//if the message is configured to communicate through ECC queue, either
//by setting a MID server or calling executeAsync, one needs to set skip_sensor
//to true. Otherwise, one may get an intermittent error that the response body is null
//r.setEccParameter('skip_sensor', true);

 var response = r.execute();
 gs.debug('Retrieved federal agency list');
 var responseBody = JSON.parse(response.getBody());
 gs.debug('Parsed the results of the federal agency list');
 var httpStatus = response.getStatusCode();
 var agencyListObj = new GlideRecord('x_58872_needit_federal_agency_list');
 if ( httpStatus === 200 ) {
	 gs.debug('Federal Register API call was successful');
	 responseBody.forEach( function(agency) {
		 gs.debug('Retrieving agency: ' + agency.name);
		 agencyListObj.initialize();
		 agencyListObj.agency_sn_id = agency.id;
		 agencyListObj.agency_name = agency.name;
		 agencyListObj.agency_url = agency.url;
		 agencyListObj.description = agency.description;
		 agencyListObj.insert();
	 });
 }
}
catch(ex) {
 var message = ex.getMessage();
 gs.error('Encountered an error ' + message);
}
